$p.events.on_editor_load =  function () {
    console.log("編集画面読み込み時の必須項目の変更");
    if($("#HeaderTitle").text() !== '新規作成'){
        // xxx 非活性
        $('#Issues_Status').prop('disabled', true).css('background-color','#f5f5f5');
        // $('#Issues_StatusField label').addClass('required ');
    }
    switch($("#Issues_Status").val()) {
	    // 申請待ち
        case '100':
            // xxx 活性 必須
            console.log("xxxx");
            break;
	    // 申請部門承認待
        case '200':
            // 申請者 非活性
			$('#Issues_Owner').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動元部門 非活性
			$('#Issues_ClassA').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動先部門 非活性
			$('#Issues_ClassE').prop('disabled', true).css('background-color','#f5f5f5');
            // 申請日 非活性
			$('#Issues_DateA').prop('disabled', true).css('background-color','#f5f5f5');
            // 起案理由 非活性
			$('#Issues_DescriptionA').prop('disabled', true).css('background-color','#f5f5f5');			
            break;
	    // 受入部門承認待
        case '300':
            // 申請者 非活性
			$('#Issues_Owner').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動元部門 非活性
			$('#Issues_ClassA').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動先部門 非活性
			$('#Issues_ClassE').prop('disabled', true).css('background-color','#f5f5f5');
            // 申請日 非活性
			$('#Issues_DateA').prop('disabled', true).css('background-color','#f5f5f5');
            // 起案理由 非活性
			$('#Issues_DescriptionA').prop('disabled', true).css('background-color','#f5f5f5');		
            break;
	    // 人事部門承認待
        case '400':
            // 申請者 非活性
			$('#Issues_Owner').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動元部門 非活性
			$('#Issues_ClassA').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動先部門 非活性
			$('#Issues_ClassE').prop('disabled', true).css('background-color','#f5f5f5');
            // 申請日 非活性
			$('#Issues_DateA').prop('disabled', true).css('background-color','#f5f5f5');
            // 起案理由 非活性
			$('#Issues_DescriptionA').prop('disabled', true).css('background-color','#f5f5f5');			
            break;
	    // 取下
        case '910':
            // 申請者 非活性
			$('#Issues_Owner').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動元部門 非活性
			$('#Issues_ClassA').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動先部門 非活性
			$('#Issues_ClassE').prop('disabled', true).css('background-color','#f5f5f5');
            // 申請日 非活性
			$('#Issues_DateA').prop('disabled', true).css('background-color','#f5f5f5');
            // 起案理由 非活性
			$('#Issues_DescriptionA').prop('disabled', true).css('background-color','#f5f5f5');		
            break;
	    // 完了
        case '920':
            // 申請者 非活性
			$('#Issues_Owner').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動元部門 非活性
			$('#Issues_ClassA').prop('disabled', true).css('background-color','#f5f5f5');
            // 異動先部門 非活性
			$('#Issues_ClassE').prop('disabled', true).css('background-color','#f5f5f5');
            // 申請日 非活性
			$('#Issues_DateA').prop('disabled', true).css('background-color','#f5f5f5');
            // 起案理由 非活性
			$('#Issues_DescriptionA').prop('disabled', true).css('background-color','#f5f5f5');		

            break;
        default:
            break;
    }

}

